import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { MessageCircle } from 'lucide-react';

const Navigation = () => {
  const location = useLocation();
  const isContactPage = location.pathname === '/contact';

  const navItems = [
    { href: '#soil', icon: Sprout, label: 'Soil', emoji: '🌱' },
    { href: '#irrigation', icon: Droplets, label: 'Irrigation', emoji: '💧' },
    { href: '#weather', icon: Cloud, label: 'Weather', emoji: '🌤️' },
    { href: '#light', icon: Lightbulb, label: 'Light', emoji: '💡' },
    { href: '#camera', icon: Camera, label: 'Camera', emoji: '📹' },
    { href: '#security', icon: Shield, label: 'Security', emoji: '🛡️' },
    { href: '#about', icon: Users, label: 'About', emoji: '👩‍💻' },
  ];

  return (
    <motion.nav 
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.8, delay: 0.2 }}
      className="fixed top-6 left-1/2 transform -translate-x-1/2 z-40 w-full max-w-6xl px-6"
    >
      <div className="backdrop-blur-xl bg-dark-800/80 border border-primary-500/20 rounded-2xl px-8 py-4 shadow-2xl">
        <div className="flex items-center justify-between">
          <Link to="/" className="flex items-center space-x-4 group">
            <div className="relative">
              <img 
                src="/assets/images/agri-bot_logo.png" 
                alt="Agri-Bot Logo" 
                className="w-12 h-12 rounded-full border-2 border-primary-500 shadow-lg transition-transform duration-300 group-hover:scale-110" 
              />
              <div className="absolute -inset-1 bg-primary-500/20 rounded-full blur animate-pulse"></div>
            </div>
            <span className="text-2xl font-bold bg-gradient-to-r from-primary-400 to-primary-600 bg-clip-text text-transparent">
              Agri-Bot
            </span>
          </Link>
          
          {!isContactPage ? (
            <ul className="hidden md:flex items-center space-x-1">
              {navItems.map((item, index) => (
                <motion.li 
                  key={item.href}
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: 0.1 * index }}
                >
                  <a 
                    href={item.href} 
                    className="px-4 py-2 rounded-xl text-gray-300 hover:text-primary-400 hover:bg-primary-500/10 transition-all duration-300 font-medium flex items-center space-x-2 group"
                  >
                    <span className="text-lg group-hover:scale-110 transition-transform duration-300">
                      {item.emoji}
                    </span>
                    <span>{item.label}</span>
                  </a>
                </motion.li>
              ))}
              <motion.li
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.8 }}
              >
                <Link 
                  to="/contact" 
                  className="px-6 py-2 bg-gradient-to-r from-primary-500 to-primary-600 text-white rounded-xl hover:from-primary-600 hover:to-primary-700 transition-all duration-300 font-medium shadow-lg hover:shadow-primary-500/25 transform hover:scale-105 flex items-center space-x-2"
                >
                  <MessageCircle size={18} />
                  <span>Contact</span>
                </Link>
              </motion.li>
            </ul>
          ) : (
            <Link 
              to="/" 
              className="px-6 py-2 bg-gradient-to-r from-primary-500 to-primary-600 text-white rounded-xl hover:from-primary-600 hover:to-primary-700 transition-all duration-300 font-medium shadow-lg hover:shadow-primary-500/25 transform hover:scale-105"
            >
              Back to Dashboard
            </Link>
          )}
        </div>
      </div>
    </motion.nav>
  );
};

export default Navigation;
